creator = #{creator}, createDate = #{createDate}, lastModifier = #{lastModifier}, lastModDate = #{lastModDate}, status = #{status}, categoryId = #{categoryId}, name = #{name}, adPic = #{adPic}, picLink = #{picLink},
WHERE id = #{id}

