mapper.update:
UPDATE system_menu


        creator = #{update.creator},


        createDate = #{update.createDate},


        lastModifier = #{update.lastModifier},


        lastModDate = #{update.lastModDate},


        status = #{update.status},


        name = #{update.name},


        parentId = #{update.parentId},


        nodeOrder = #{update.nodeOrder},


        nodeLevel = #{update.nodeLevel},


        menuUrl = #{update.menuUrl},


        code = #{update.code},





        AND id = #{condition.id}


        AND creator = #{condition.creator}


        AND createDate = #{condition.createDate}


        AND lastModifier = #{condition.lastModifier}


        AND lastModDate = #{condition.lastModDate}


        AND status = #{condition.status}


        AND name = #{condition.name}


        AND parentId = #{condition.parentId}


        AND nodeOrder = #{condition.nodeOrder}


        AND nodeLevel = #{condition.nodeLevel}


        AND menuUrl = #{condition.menuUrl}


        AND code = #{condition.code}

UPDATE system_menu
creator = #{creator}, createDate = #{createDate}, lastModifier = #{lastModifier}, lastModDate = #{lastModDate}, status = #{status}, name = #{name}, parentId = #{parentId}, nodeOrder = #{nodeOrder}, nodeLevel = #{nodeLevel}, menuUrl = #{menuUrl}, code = #{code},
WHERE id = #{id}

mapper.delete:
DELETE FROM system_menu


        AND id = #{id}


