        AND createDate = #{createDate}


        AND lastModifier = #{lastModifier}


        AND lastModDate = #{lastModDate}


        AND status = #{status}


        AND name = #{name}


        AND parentId = #{parentId}


        AND nodeOrder = #{nodeOrder}


        AND nodeLevel = #{nodeLevel}


        AND menuUrl = #{menuUrl}


        AND code = #{code}

