UPDATE shop_order_commodity_list
creator = #{creator}, createDate = #{createDate}, lastModifier = #{lastModifier}, lastModDate = #{lastModDate}, status = #{status}, commodityId = #{commodityId}, orderId = #{orderId}, number = #{number}, price = #{price},
WHERE id = #{id}

